import React from 'react';

const Categories = ({ categories, filterItems }) => {
  return (
    <div className='btn-container'>
      {categories.map((category) => {
        return (
          <button className='filter-btn' onClick={() => filterItems('All')}>
            All
          </button>
        );
      })}
    </div>
  );
};

export default Categories;
