import React, { useEffect } from 'react';

const Alert = ({ message, type, removeAlert }) => {
  useEffect(() => {
    const alert = setTimeout(() => {}, [3000]);
    return () => clearTimeout(alert);
  }, [alert]);

  return <p className={`alert alert-${type}`}>{message}</p>;
};

export default Alert;
